try {
  const input = $node["Scrape LinkedIn Profile - Apify"].json;

  console.log('Experiences node - Input type:', Array.isArray(input) ? 'Array' : typeof input);

  // Apify returns single object with experiences field
  const experiences = input?.experiences || [];

  console.log('Experiences found:', experiences.length);

  // Build the HTML table rows string
  let htmlRows = '';
  if (Array.isArray(experiences) && experiences.length > 0) {
    experiences.forEach(exp => {
      htmlRows += `<tr>
        <td>${exp.subtitle || exp.companyName || 'N/A'}</td>
        <td>${exp.title || 'N/A'}</td>
        <td>${exp.caption || 'N/A'}</td>
        <td>${exp.metadata || 'N/A'}</td>
      </tr>`;
    });
  } else {
    htmlRows = '<tr><td colspan="4">No experience data available</td></tr>';
  }

  console.log('Generated experiences HTML rows:', htmlRows.length, 'characters');

  // Return a new object that only contains the experiencesTable field
  return [{ json: { experiencesTable: htmlRows } }];

} catch (error) {
  console.error('Error in Experiences node:', error);
  return [{ json: { experiencesTable: `<tr><td colspan="4">Error processing experiences: ${error.message}</td></tr>` } }];
}

