try {
  const input = $node["Scrape LinkedIn Profile - Apify"].json;

  console.log('Posts node - Full input structure:', JSON.stringify(input, null, 2));

  // Apify returns single object with updates field
  const posts = input?.updates || [];

  console.log('Posts found:', posts.length);

  // Build HTML for each LinkedIn post
  let postsHTML = '';
  if (Array.isArray(posts) && posts.length > 0) {
    posts.forEach(post => {
      postsHTML += `<div class="linkedin-post" style="border:1px solid #ddd; padding:10px; margin-bottom:10px;">
        <p>${post.postText || 'No content'}</p>
        <p><em>Likes: ${post.numLikes || 0} | Comments: ${post.numComments || 0}</em></p>
      </div>`;
    });
  } else {
    postsHTML = '<div class="linkedin-post">No recent posts available</div>';
  }

  console.log('Generated posts HTML length:', postsHTML.length);

  // Return only the generated HTML
  return [{ json: { postsHTML } }];

} catch (error) {
  console.error('Error in Posts node:', error);
  return [{ json: { postsHTML: '<div class="linkedin-post">Error loading posts</div>' } }];
}

