try {
  // Process the duplicate report check result
  const reportCheckResponse = $json;
  const leadData = $('Process Lead Check Result').first().json;

  console.log('=== PROCESS DUPLICATE REPORT CHECK ===');
  console.log('Lead data:', JSON.stringify(leadData, null, 2));
  console.log('Report check response:', JSON.stringify(reportCheckResponse, null, 2));

  // Check if response is an array and has existing reports
  if (Array.isArray(reportCheckResponse) && reportCheckResponse.length > 0) {
    // Research report already exists - LOG WARNING but CONTINUE
    const existingReport = reportCheckResponse[0];
    console.log('‚ö†Ô∏è Research report already exists:', existingReport.id);
    console.log('Created at:', existingReport.created_at);
    console.log('üîÑ Proceeding with re-generation (will create new report)');
    console.log('=== END DUPLICATE CHECK ===');

    // Add flag to indicate this is a re-generation
    leadData.is_regeneration = true;
    leadData.existing_report_id = existingReport.id;
  } else {
    // No duplicate found - proceed with research generation
    console.log('‚úÖ No duplicate research report found. Proceeding with generation.');
    console.log('=== END DUPLICATE CHECK ===');

    leadData.is_regeneration = false;
  }

  return [{ json: leadData }];

} catch (error) {
  console.error('Error in Process Duplicate Report Check:', error);
  throw new Error(`Duplicate Check Error: ${error.message}`);
}

