try {
  // Process the lead check result from Supabase
  const leadCheckResponse = $json;
  // Try to get LinkedIn URL from multiple sources
  let linkedinUrl;
  try {
    linkedinUrl = $('Normalize LinkedIn URL').first().json.normalized_url;
  } catch (error) {
    // Fallback: extract from profile data or use hardcoded test URL
    const profileData = $('Scrape LinkedIn Profile - Apify').last().json;
    linkedinUrl = profileData?.linkedinUrl || 'https://www.linkedin.com/in/satyanadella';
    console.log('Using fallback LinkedIn URL:', linkedinUrl);
  }
  const profileData = $('Scrape LinkedIn Profile - Apify').last().json;

  console.log('=== PROCESS LEAD CHECK RESULT ===');
  console.log('LinkedIn URL:', linkedinUrl);
  console.log('Lead check response:', JSON.stringify(leadCheckResponse, null, 2));

  // Check if response is an array and has leads
  if (!Array.isArray(leadCheckResponse) || leadCheckResponse.length === 0) {
    console.log('‚ùå No lead found for URL:', linkedinUrl);
    console.log('Creating minimal lead record for research workflow...');

    // Create a minimal lead record using profile data
    const newLead = {
      id: 'temp-' + Date.now(), // Temporary ID for research workflow
      full_name: profileData?.fullName || 'Unknown',
      company_name: profileData?.companyName || 'Unknown',
      job_title: profileData?.jobTitle || 'Unknown',
      linkedin_url: linkedinUrl,
      user_id: 'google-103971781553190740426',
      created_for_research: true // Flag to indicate this was created for research
    };

    console.log('‚úÖ Created temporary lead for research:', newLead.id, '-', newLead.full_name);
    console.log('=== END PROCESS LEAD CHECK ===');

    return [{ json: newLead }];
  }

  // Lead exists - get the first (and should be only) lead
  const existingLead = leadCheckResponse[0];
  console.log('‚úÖ Lead found:', existingLead.id, '-', existingLead.full_name);
  console.log('=== END PROCESS LEAD CHECK ===');

  return [{ json: existingLead }];

} catch (error) {
  console.error('Error in Process Lead Check Result:', error);
  throw new Error(`Lead Check Error: ${error.message}`);
}

try {
  // Simplified data preparation for development/testing
  const htmlReport = $('Create HTML Report').last().json.html;
  const profileData = $('Scrape LinkedIn Profile - Apify').last().json;

  console.log('=== PREPARE RESEARCH REPORT DATA (SIMPLIFIED) ===');
  console.log('HTML report length:', htmlReport ? htmlReport.length : 'No HTML report');
  console.log('Profile data available:', !!profileData);

  // Generate proper UUID v4 format for temporary lead ID
  function generateUUID() {
    return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
      const r = Math.random() * 16 | 0;
      const v = c == 'x' ? r : (r & 0x3 | 0x8);
      return v.toString(16);
    });
  }
  const tempLeadId = generateUUID();

  // Extract lead name from profile data
  const leadName = profileData?.fullName || 'Unknown Lead';

  console.log('Generated temp lead ID:', tempLeadId);
  console.log('Extracted lead name:', leadName);

  // Prepare simplified research report data for database insertion
  const reportData = {
    user_id: 'google-103971781553190740426', // SharpFlow user ID
    lead_id: tempLeadId, // Temporary lead ID for development
    lead_name: leadName,
    report_html: htmlReport || '<p>No research report generated</p>',
    relevance_score: 0 // Default score
  };

  console.log('Generated report data (without HTML content):');
  console.log({
    ...reportData,
    report_html: `[HTML content - ${reportData.report_html.length} characters]`
  });
  console.log('=== END PREPARE RESEARCH REPORT DATA ===');

  return [{ json: reportData }];

} catch (error) {
  console.error('Error in Prepare Research Report Data:', error);
  throw new Error(`Report Data Preparation Error: ${error.message}`);
}
